package com.boot.dh.entity;import java.util.Date;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;@Entity@Table(name="blogcomment")public class BlogComment implements java.io.Serializable {	 	private static final long serialVersionUID = -6849759789881813896L;	@Id	@GeneratedValue(strategy=GenerationType.IDENTITY)	private long id;		@Column(name="ip")	private String ip;		@Column(name="content")	private String content;		@Column(name="comment_time")	private Date commentTime;		@Column(name="is_use")	private int isUse;/** 是否通过 1-是 0-否 **/		@Column(name="blogId")	public long blogId;		public BlogComment(){}	public BlogComment(long id, String ip, String content, Date commentTime, int isUse,long blogId) {		super();		this.id = id;		this.ip = ip;		this.content = content;		this.commentTime = commentTime;		this.isUse = isUse;		this.blogId=blogId;	}		public long getBlogId() {		return blogId;	}	public void setBlogId(long blogId) {		this.blogId = blogId;	}	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getIp() {		return ip;	}	public void setIp(String ip) {		this.ip = ip;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public Date getCommentTime() {		return commentTime;	}	public void setCommentTime(Date commentTime) {		this.commentTime = commentTime;	}	public int getIsUse() {		return isUse;	}	public void setIsUse(int isUse) {		this.isUse = isUse;	}		}