package com.boot.dh.entity;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;@Entitypublic class Link implements java.io.Serializable {	/**  	 * serialVersionUID:TODO(用一句话描述这个变量表示什么).  	 * @since JDK 1.6  	 */	 	private static final long serialVersionUID = -2486174099142097781L;	@Id	@GeneratedValue(strategy=GenerationType.IDENTITY)	private long id;	@Column(name="name")		private String name;		@Column(name="url")	private String url;		@Column(name="isUse")	private boolean isUse;		public Link(){}	public Link(long id, String name, String url, boolean isUse) {		super();		this.id = id;		this.name = name;		this.url = url;		this.isUse = isUse;	}	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getUrl() {		return url;	}	public void setUrl(String url) {		this.url = url;	}	public boolean getIsUse() {		return isUse;	}	public void setIsUse(boolean isUse) {		this.isUse = isUse;	}	}